import {useState,useEffect} from 'react'
import axios from 'axios'
import './Student_Attendance_Take.css'


const Student_Attendance_Take = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [user, setUser] = useState({});
    useEffect(() => {
        const token = localStorage.getItem('token');
        if(token){
        setLoggedIn(true);
        setUser(JSON.parse(localStorage.getItem('user')));
        }
        else{
            alert("Please Login First")
            window.location.href = '/login'
        }
    },[]);
    useEffect(() => {
        const getFlag = async () => {
            const res = await axios.get('http://localhost:5000/api/getOverride');
            console.log(res.data.master.master_override);
            if(res.data.master.master_override){
                alert("Attendance is not allowed now")
                window.location.href = '/student_dashboard'
            }
        }
    });
  return (
    <div>
        <h1>Take Attendance</h1>
    </div>
  )
}

export default Student_Attendance_Take
