import {useState,useEffect} from 'react'

const Take_Attendance = () => {
    const [loggedIn, setLoggedIn] = useState(false);
    const [user, setUser] = useState({});
    const [startAttendance,setStartAttendance] = useState(false)
    useEffect(() => {
        const token = localStorage.getItem('token');
        if(token){
        setLoggedIn(true);
        setUser(JSON.parse(localStorage.getItem('user')));
        }
        else{
            alert("Please Login First")
            window.location.href = '/login'
        }
    },[])

    use
    const handleChanges = (e) => {
        e.preventDefault()
        setStartAttendance(!startAttendance)

    }
  return (
    <div>
        <h1>Take Attendance</h1>
        <strong>Date : </strong> "Date"
        <br/>
        <strong>Time : </strong> {new Date().toLocaleTimeString()}
        <br/>
        <strong>Subject : </strong> "Subject"
        <p></p>
        <input type='button' value={ startAttendance ? 'Stop Attendance' : 'Start Attendance'} onClick={handleChanges}/>

    </div>
  )
}

export default Take_Attendance
