import {useState,useEffect} from 'react'

const Student_Attendance_View = () => {
  const [isLoggedin, setIsLoggedin] = useState(false)
  const [user, setUser] = useState({})
  const [attendance, setAttendance] = useState([])
  const [attendanceDate, setAttendanceDate] = useState(new Date().toLocaleDateString())
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'))
    setAttendance(new Date().toLocaleDateString())
    console
    if (user) {
      setUser(user)
      setIsLoggedin(true)
    }
    else{
      setIsLoggedin(false)
      alert("Please Login First")
      window.location.href="/login"
    }
  },[])

  return (
    <div>
      <div>Seletct Date : </div>
      <input type="date"  placeholder={attendanceDate}></input>
    </div>
      
  )
}

export default Student_Attendance_View
