import {useState,useEffect} from 'react'

const Student_Attendance_View = () => {
  const [isLoggedin, setIsLoggedin] = useState(false)
  const [user, setUser] = useState({})
  const [attendance, setAttendance] = useState([])
  const [attendanceDate, setAttendanceDate] = useState()
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'))
    //set date in the form of yyyy-mm-dd
    let date = new Date()
    let date1 = date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate()
    
    console.log(date1)
    setAttendance(date1)
    
    if (user) {
      setUser(user)
      setIsLoggedin(true)
    }
    else{
      setIsLoggedin(false)
      alert("Please Login First")
      window.location.href="/login"
    }
  },[])

  return (
    <div>
      <div>Seletct Date : </div>
      <input type="date"  value={attendanceDate} onChange={(e)=>{ console.log(e.target.value)}}></input>
    </div>
      
  )
}

export default Student_Attendance_View
